INSERT INTO `t_m_diagnosa_form` VALUES ('D0039', 'Risiko Gangguan Integritas Kulit', 'D.0039', 'Berisiko mengalami ketidak cukupan aliran darah ke jaringan tubuh, yang dapat mengakibatkan disfungsi seluler yang mengancam jiwa', 'M_D007', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0040', 'Gangguan Eliminasi Urin', 'D.0040', 'Disfungsi eliminasi urin', 'M_D004', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0049', 'Konstipasi', 'D.0049', 'Penurunan defekasi normal yang disertai pengeluaran feses sulit dan tidak tuntas serta feses kering dan banyak.', 'M_D004', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0050', 'Retensi Urin', 'D.0050', 'Pengosongan kandung kemih yang tidak lengkap.', 'M_D004', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0054', 'Gangguan Mobilitas Fisik', 'D.0054', 'Keterbatasan dalam gerakan fisik dari satu atau lebih ekstremitas secara mandiri.', 'M_D005', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0055', 'Gangguan Pola Tidur', 'D.0055', 'Gangguan kualitas dan kuantitas waktu tidur akibat faktor eksternal', 'M_D005', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0056', 'Intoleransi Aktivitas', 'D.0056', 'Ketidakcukupan energi untuk melakukan aktivitas sehari-hari.', 'M_D005', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0062', 'Gangguan Memori', 'D.0062', 'Ketidakmampuan mengingat beberapa informasi atau perilaku.', 'M_D006', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0063', 'Gangguan Menelan', 'D.0063', 'Fungsi menelan abnormal akibat defisit struktur atau fungsi oral, faring atau esofagus', 'M_D006', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0064', 'Konfusi Akut', 'D.0064', 'Gangguan kesadaran, perhatian, kognitif, dan persepsi yang reversibel, berlangsung tiba-tiba dan singkat', 'M_D006', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0065', 'Konfusi Kronis', 'D.0065', 'Gangguan kesadaran, perhatian, kognitif dan persepsi yang ireversibel, berlangsung lama, dan/atau progresif', 'M_D006', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0069', 'Disfungsi Seksual', 'D.0069', 'Perubahan fungsi seksual selama fase respon seksual berupa hasrat terangsang, orgasme, dan/atau relaksasi yang dirasa tidak memuaskan, tidak bermakna atau tidak adekuat.', 'M_D007', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0070', 'Kesiapan Persalinan', 'D.0070', 'Pola mempersiapkan, mempertahankan dan memperkuat proses kehamilan dan persalinan serta perawatan bayi baru lahir.', 'M_D007', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0071', 'Pola Seksual Tidak Efektif', 'D.0071', 'Kekhawatiran individu melakukan hubungan seksual yang berisiko menyebabkan perubahan kesehatan', 'M_D007', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0074', 'Gangguan Rasa Nyaman', 'D.0074', 'Perasaan kurang senang, lega dan sempurna dalam dimensi fisiki, psikospiritual, lingkungan dan sosial.', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0075', 'Ketidaknyamanan Pasca Partum', 'D.0075', 'Perasaan tidak nyaman yang berhubungan dengan kondisi setelah melahirkan.', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0076', 'Nausea', 'D.0076', 'Perasaan tidak nyaman pada bagian belakang tenggorok atau lambung yang dapat mengakibatkan muntah.', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0077', 'Nyeri Akut', 'D.0077', 'Pengalaman sensorik atau emosional yang berkaitan dengan kerusakan jaringan aktual atau fungsional, dengan onset mendadak atau lambat dan berintensitas ringan hingga berat yang berlangsung kurang dari 3 bulan', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0078', 'Nyeri Kronis', 'D.0078', 'Pengalaman sensorik atau emosional yang berkaitan dengan kerusakan jaringan aktual atau fungsional, dengan onset mendadak atau lambat dan berintensitas ringan hingga berat yang berlangsung lebih dari 3 bulan.', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0079', 'Nyeri Melahirkan', 'D.0079', 'Pengalaman sensorik dan emosional yang bervariasi dari menyenangkan sampai tidak menyenangkan yang berhubungan dengan persalinan.', 'M_D008', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0080', 'Ansietas', 'D.0080', 'Kondisi emosi dan pengalaman subyektif individu terhadap objek yang tidak jelas dan spesifik akibat antisipasi bahaya yang memungkikan individu melakukan tindakan untuk menghadapi ancaman', 'M_D009', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0081', 'Berduka', 'D.0081', 'Respon psikososial yang ditunjukkan oleh klien akibat kehilangan (orang, objek, fungsi, status, bagian tubuh atau hubungan).', 'M_D009', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0085', 'Gangguan Persepsi Sensori', 'D.0085', 'Perubahan persepsi terhadap stimulus baik internal maupun eksternal yang disertai dengan respon yang berkurang, berlebihan atau terdistorsi.', 'M_D009', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0106', 'Gangguan Tumbuh Kembang', 'D.0106', 'Kondisi individu mengalami gangguankemampuan bartumbuh dan berkembang sesuai dengan kelompok usia.', 'M_D010', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0109', 'Defisit Perawatan Diri', 'D.0109', 'Tidak mampu melakukan atau menyelesaikan aktivitas perawatan diri.', 'M_D011', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0111', 'Defisit Pengetahuan', 'D.0111', 'Ketiadaan atau kurangnya informasi kognitif yang berkaitan dengan topik tertentu.', 'M_D012', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0118', 'Gangguan Interaksi Sosial', 'D.0118', 'Kuantitas dan/ atau kualitas hubungan sosial yang kurang atau berlebih', 'M_D013', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0119', 'Gangguan Komunikasi Verbal', 'D.0119', 'Penurunan, perlambatan atau ketiadaan kemampuan untuk menerima, memproses, mengirim, dan/atau menggunakan sistem simbol.', 'M_D013', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0129', 'Gangguan Integritas Kulit-Jaringan', 'D.0129', 'Kerusakan kulit (dermis dan/atau epidermis) atau jaringan (mmebran mukosa, kornea, fasia, otot, tendon, tulang, kartilago, kapsul sendir dan/atau ligamen).', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0130', 'Hipertemia', 'D.0130', 'Suhu tubuh meningkat di atas rentang normal tubuh.', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0131', 'Hiportemia', 'D.0131', 'Suhu tubuh berada dibawah rentang normal tubuh.', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0142', 'Risiko Infeksi', 'D.0142', 'Berisiko mengalami peningkatan terserang organisme patogenik.', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0143', 'Risiko Jatuh', 'D.0143', 'Berisiko mengalami kerusakan fisik dan gangguan kesehatan akibat terjatuh.', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0144', 'Risiko Luka Tekan', 'D.0144', 'Berisiko mengalami cedera lokal pada kulit dan/ atau jaringan, biasanya pada tonjolan akibat tekanan dan/atau gesekan.', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0148', 'Risiko Termoregulasi Tidak Efektif', 'D.0148', 'Berisiko mengalami kegagalan mempertahankan suhu tubuh dalam rentang normal', 'M_D014', '0');
INSERT INTO `t_m_diagnosa_form` VALUES ('D0149', 'Termoregulasi Tidak Efektif', 'D.0149', 'Kegagalan mempertahankan suhu tubuh dalam rentang normal.', 'M_D014', '0');
